import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Card, Container, Form } from 'react-bootstrap';

class ViewQuestion extends Component {
  render() {
    const { authedUser, users, questions } = this.props;
    console.log('View::');
    // console.log(users);
    console.log(this.props);
    return (
      <Container className="view-que-container">
        <Card expand="sm" className="que-card">
          <Card.Header>
            <Card.Title className="primary-text"></Card.Title>
          </Card.Header>
          <Card.Body className="que-card-body">
            <div className="avatar-wrapper">
              {/* <img className="avatar" src={"../../assets/images/" + users[question.author].avatarURL} alt="avatar" /> */}
            </div>
            <div className="divider"></div>
            <div className="content-wrapper">
              <Card.Title>Would you rather ...</Card.Title>
              <Form>
                <Form.Group>
                  <Form.Check type="radio" label="Option One"></Form.Check>
                </Form.Group>
                <Form.Group>
                  <Form.Check type="radio" label="Option Two"></Form.Check>
                </Form.Group>
                <Button type="submit">Submit</Button>
              </Form>
            </div>

          </Card.Body>
        </Card>
      </Container>
    );
  }
}

const mapStateToProps = (authedUser, users, questions) => {
  console.log('View Que::');
  console.log(users);
  console.log(questions);
  return {
    authedUser,
    users,
    questions
  }
}

export default connect(mapStateToProps)(ViewQuestion);
