import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Container } from 'react-bootstrap';

class Question extends Component {
  render() {
    const { users, question } = this.props;
    console.log('PROPS::');
    console.log(users);
    console.log(question);
    return (
      <Container className="que-container">
        <Card expand="sm" className="que-card">
          <Card.Header>
            <Card.Title>{users[question.author].name} asks:</Card.Title>
          </Card.Header>
          <Card.Body>
            <div>
              <img className="avatar" src={"../../assets/images/" + users[question.author].avatarURL} alt="avatar" />
              <div>{question.author}</div>
            </div>
            <div>
              <h4>Would you rather</h4>
              <h5>{question.optionOne}</h5>
              <h5>{question.optionTwo}</h5>
            </div>

          </Card.Body>
        </Card>
      </Container>
    );
  }
}

const mapStateToProps = ({ authedUser, questions, users, id }) => {
  // const question = questions[id];
  console.log('Data >> ');
  console.log(questions);
  console.log(users);
  return {
    authedUser,
    users,
    questions
  }
}

export default connect(mapStateToProps)(Question);
