import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Card, ProgressBar } from 'react-bootstrap';

class Answered extends Component {
  render() {
    const { question, options, selected, totalVotes } = this.props
    let per;
    console.log('Answered::');
    // console.log(question.optionOne.votes);
    // console.log(question.optionTwo.votes);
    return (
      <Fragment>
        <Card.Title>Results:</Card.Title>
        {
          options.map((option) => (
            per = Math.floor(option.votes.length / totalVotes * 100),
            <div className="box-container" key={option.text}>
              <Card.Subtitle>{option.text}</Card.Subtitle>
              <ProgressBar now={per} label={per} />
              <Card.Text>{option.votes.length} of {totalVotes} votes</Card.Text>
            </div>
          ))
        }
      </Fragment>
    )
  }
}

const mapStateToProps = ({ authedUser }, { question }) => {
  console.log('log::');
  console.log(question);
  console.log(question.optionOne.votes.length);
  console.log(question.optionTwo.votes.length);

  return {
    question,
    options: [question.optionOne, question.optionTwo],
    selected: authedUser.answers[question.id],
    totalVotes: question.optionOne.votes.length + question.optionTwo.votes.length
  }
}

export default connect(mapStateToProps)(Answered);
