import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Container, Image, Nav } from 'react-bootstrap';
import Unanswered from './Unanswered';
import Answered from './Answered';
import NotFound from '../NotFound';
import './ViewQuestion.css';
import { LinkContainer } from 'react-router-bootstrap';
import { Redirect, Route } from 'react-router-dom';

class ViewQuestion extends Component {
  render() {
    const { users, question } = this.props;

    if (!question) {
      // return <NotFound />;
      return (
        // <Nav activeKey={window.location.pathname}>
        //   <LinkContainer to="/404">
        //     {/* <Nav.Link >Home</Nav.Link> */}
        //     <NotFound eventKey="404" />
        //   </LinkContainer>
        // </Nav>
        <Redirect push to='/404' />
        // <Route exact path="/404" component={NotFound} />
      )
    }

    return (
      <Container className="view-que-container">
        <Card expand="sm" className="que-card">
          <Card.Header>
            <Card.Title className="primary-text">
              {users[question.author].name}
              {
                !this.props.answered ?
                  ' asks:' :
                  ' asked:'
              }
            </Card.Title>
          </Card.Header>
          <Card.Body className="que-card-body">
            <div className="avatar-wrapper">
              <Image
                className="avatar"
                src={"../../assets/images/" + users[question.author].avatarURL}
                alt="avatar" />
            </div>
            <div className="divider"></div>
            <div className="content-wrapper">
              {
                !this.props.answered ?
                  <Unanswered question={question} /> :
                  <Answered question={question} />
              }
            </div>

          </Card.Body>
        </Card>
      </Container>
    );
  }
}

const mapStateToProps = ({ authedUser, users, questions }, props) => {
  const id = props.match.params.id;
  return {
    users,
    question: questions[id],
    answered: authedUser.answers[id]
  }
}

export default connect(mapStateToProps)(ViewQuestion);
