          <Card.Subtitle>{question.optionOne.text}</Card.Subtitle>
          <ProgressBar now={40} label='40%' />
          <Card.Text>some text</Card.Text>
        </div>
        <div className="box-container">
          <Card.Subtitle>{question.optionTwo.text}</Card.Subtitle>
          <ProgressBar now={60} label='60%' />
          <Card.Text>some text</Card.Text>
        </div>
      </Fragment>
    )
  }
}

const mapStateToProps = ({ authedUser }, { question }) => {
  console.log('log::');
  console.log(question);
  const options = [question.optionOne, question.optionTwo];
  console.log(options);
  return {
    question,
    options,
    selected: authedUser.answers[question.id],
    totalVotes: question.optionOne.votes + question.optionTwo.votes
  }
}

export default connect(mapStateToProps)(Answered);
